TestCase.SQLScript:
        -- Check courseName. The result should be null
        set @courseName = 'new course';
	    select * from Courses where courseName = @courseName;
        -- Check courseNumber. The result should be null
        set @courseNumber = 1000;
	    select * from Courses where courseNumber = @courseNumber;
        -- Check majorId. The result should be null
        set @majorId = 4;
    	select * from Majors where majorId = @majorId;
        -- Try to add a course with the new courseName, courseNumber and majorId
        insert into Courses (courseId, courseName, courseNumber, majorId, scheduleId, stateId, description) values (UUID(), @courseName, @courseNumber, @majorId, 1, 'On', 'test major');
