TestCase.SQLScript:
-- Check courseName. The result should be null
set @courseName = 'new course';
select * from Courses where courseName = @courseName;
-- Check courseNumber. The result should be null
set @courseNumber = 1000;
select * from Courses where courseNumber = @courseNumber;
-- Check scheduleId. The result should be null
set @scheduleId = 4;
select * from Schedules where scheduleId = @scheduleId;
-- Try to add a course with the new courseName, courseNumber and scheduleId
insert into Courses (courseId, courseName, courseNumber, majorId, scheduleId, stateId, description) values (UUID(), @courseName, @courseNumber, 2, @scheduleId, 'On', 'test schedule');
